    /*
     * Get administrador by id
     */
    function get_administrador($id)
    {
        return $this->db->get_where('administradores', array('id' => $id))->row_array();
    }

    /*
     * Get all administradores count
     */
    function get_all_administradores_count()
    {
        $this->db->from('administradores');
        return $this->db->count_all_results();
    }

    /*
     * Get all administradores
     */
    function get_all_administradores($params = array())
    {
        $this->db->order_by('id', 'desc');
        if (isset($params) && !empty($params)) {
            $this->db->limit($params['limit'], $params['offset']);
        }
        return $this->db->get('administradores')->result_array();
    }

