    }

    /*
     * function to update administrador
     */
    function update_administrador($id, $params)
    {
        $this->db->where('id', $id);
        return $this->db->update('administradores', $params);
    }

    /*
     * function to delete administrador
     */
    function delete_administrador($id)
    {
        return $this->db->delete('administradores', array('id' => $id));
